import React from 'react';
import { Link } from 'react-router-dom';
import image from './image.jpg'; // Importa a imagem da pasta pages
import './Login.css'; // Estilo da página

const Login = () => {
  return (
    <div className="login-container">
      {/* Lado esquerdo com a imagem */}
      <div className="login-left">
        <img src={image} alt="Login Illustration" />
      </div>

      {/* Lado direito com o formulário */}
      <div className="login-right">
        <h1>LOGIN</h1>
        <form>
          {/* Campo de username */}
          <div className="form-group">
            <label>Usuário</label>
            <input type="text" placeholder="@mail.com" />
          </div>

          {/* Campo de senha */}
          <div className="form-group">
            <label>Senha</label>
            <input type="password" placeholder="Password" />
          </div>

          {/* Lembrar-me e esqueceu senha */}
          <div className="form-options">
            <label>
              <input type="checkbox" /> Lembrar-me
            </label>
            <Link to="/forgot-password">Esqueceu a senha?</Link>
          </div>

          {/* Botão de login */}
          <button className="btn-login" type="submit">
            Entrar
          </button>
        </form>

        {/* Link para registrar */}
        <p>
          Não tem uma conta? <Link to="/register">Registrar-se</Link>
        </p>

        {/* Login com redes sociais */}
        <div className="social-login">
          <p>Logar com</p>
          <div className="social-icons">
            <button>Facebook</button>
            <button>Google</button>
            <button>Apple</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
