import React, { useState, useEffect } from "react";
import axios from "axios";
import "./TaskList.css";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([{ id: 1, name: "Trabalho" }, { id: 2, name: "Pessoal" }]);
  const [newTask, setNewTask] = useState({ title: "", description: "", category: "", sharedWith: "" });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Função para buscar as tarefas ao carregar a página
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get("http://localhost:8000/api/tasks", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setTasks(response.data);
      } catch (err) {
        setError("Erro ao carregar tarefas.");
        console.error("Erro ao buscar tarefas:", err);
      }
    };

    fetchTasks();
  }, []);

  // Função para criar tarefa
  const handleCreateTask = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    // Verificação dos campos
    if (!newTask.category) {
      setError("Selecione uma categoria para criar a tarefa.");
      return;
    }

    if (!newTask.sharedWith) {
      setError("Adicione um ou mais usuários para compartilhar a tarefa.");
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:8000/api/tasks/create",
        newTask,
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );

      // Adicionando tarefa ao estado
      setTasks([...tasks, response.data]);
      setNewTask({ title: "", description: "", category: "", sharedWith: "" });
      setSuccess("Tarefa criada e compartilhada com sucesso!");
    } catch (err) {
      console.error("Erro ao criar tarefa:", err);
      setError("Erro ao criar tarefa.");
    }
  };

  // Função para atualizar uma tarefa
  const handleUpdateTask = async (id) => {
    try {
      const updatedTask = { ...newTask }; // Aqui você pode criar um formulário para editar a tarefa
      const response = await axios.put(
        `http://localhost:8000/api/tasks/update/${id}`,
        updatedTask,
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      const updatedTasks = tasks.map((task) =>
        task.id === id ? { ...task, ...response.data } : task
      );
      setTasks(updatedTasks);
      setSuccess("Tarefa atualizada com sucesso!");
    } catch (err) {
      console.error("Erro ao atualizar tarefa:", err);
      setError("Erro ao atualizar tarefa.");
    }
  };

  // Função para excluir tarefa
  const handleDeleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/api/tasks/delete/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      const remainingTasks = tasks.filter((task) => task.id !== id);
      setTasks(remainingTasks);
      setSuccess("Tarefa excluída com sucesso!");
    } catch (err) {
      console.error("Erro ao excluir tarefa:", err);
      setError("Erro ao excluir tarefa.");
    }
  };

  return (
    <div className="tasklist-container">
      <div className="main-content">
        <h1>Gerenciamento de Tarefas</h1>

        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}

        <div className="tasklist-box">
          <h2>Criar Nova Tarefa</h2>
          <form onSubmit={handleCreateTask}>
            <div className="form-group">
              <label>Título</label>
              <input
                type="text"
                placeholder="Título da tarefa"
                value={newTask.title}
                onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                required
              />
            </div>
            <div className="form-group">
              <label>Descrição</label>
              <input
                type="text"
                placeholder="Descrição da tarefa"
                value={newTask.description}
                onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
                required
              />
            </div>
            <div className="form-group">
              <label>Categoria</label>
              <select
                value={newTask.category}
                onChange={(e) => setNewTask({ ...newTask, category: e.target.value })}
                required
              >
                <option value="">Selecione uma categoria</option>
                {categories.map((category) => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label>Compartilhar com</label>
              <input
                type="text"
                placeholder="E-mails separados por vírgula"
                value={newTask.sharedWith}
                onChange={(e) => setNewTask({ ...newTask, sharedWith: e.target.value })}
                required
              />
            </div>
            <button className="btn-create-task" type="submit">
              Criar Tarefa
            </button>
          </form>
        </div>

        <div className="tasklist-display">
          <h2>Minhas Tarefas</h2>
          {tasks.length > 0 ? (
            <table className="tasklist-table">
              <thead>
                <tr>
                  <th>Título</th>
                  <th>Data de Entrega</th>
                  <th>Status</th>
                  <th>Tempo de Trabalho</th>
                  <th>Responsável</th>
                  <th>Ações</th>
                </tr>
              </thead>
              <tbody>
                {tasks.map((task, index) => (
                  <tr key={index}>
                    <td>{task.title}</td>
                    <td>{task.dueDate}</td>
                    <td>{task.status}</td>
                    <td>{task.timeSpent}</td>
                    <td>{task.sharedWith}</td>
                    <td>
                      <button onClick={() => handleUpdateTask(task.id)}>Editar</button>
                      <button onClick={() => handleDeleteTask(task.id)}>Excluir</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>Nenhuma tarefa encontrada.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default TaskList;
