import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // UseNavigate para navegação
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // Hook para navegação

  // Função para tratar o login
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://127.0.0.1:8000/api/token/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username: email, password }),
      });

      if (!response.ok) {
        setError('Usuário ou senha incorretos.');
        return;
      }

      const data = await response.json();
      localStorage.setItem('accessToken', data.access);
      localStorage.setItem('refreshToken', data.refresh);
      navigate('/tasks'); // Redirecionamento após login bem-sucedido
    } catch (err) {
      setError('Erro ao conectar ao servidor. Tente novamente.');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h1>Login</h1>
        {error && <p className="error-message">{error}</p>}
        <form onSubmit={handleLogin}>
          <div className="form-group">
            <label>Email</label>
            <input
              type="text"
              placeholder="Digite seu email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>Senha</label>
            <input
              type="password"
              placeholder="Digite sua senha"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="form-options">
            <label>
              <input type="checkbox" /> Lembrar-me
            </label>
            {/* Link para a página de recuperação de senha, usando navigate */}
            <a href="/forgot-password" onClick={() => navigate('/forgot-password')}>
              Esqueceu a senha?
            </a>
          </div>
          <button className="btn-login" type="submit">
            Entrar
          </button>
        </form>
        <p className="register-link">
          Não tem uma conta? <a href="/register">Registrar-se</a>
        </p>
        <div className="social-login">
          <p>Logar com</p>
          <div className="social-icons">
            <a href="https://facebook.com">
              <img src="https://cdn-icons-png.flaticon.com/512/124/124010.png" alt="Facebook" />
            </a>
            <a href="https://google.com">
              <img src="https://cdn-icons-png.flaticon.com/512/281/281764.png" alt="Google" />
            </a>
            <a href="https://apple.com">
              <img src="https://cdn-icons-png.flaticon.com/512/179/179309.png" alt="Apple" />
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
