import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ title: '', description: '', category: '', sharedWith: '' });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Carregar tarefas ao iniciar
  useEffect(() => {
    fetchTasks();
  }, []);

  // Função para buscar todas as tarefas
  const fetchTasks = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/tasks/");
      setTasks(response.data);
    } catch (err) {
      setError("Erro ao carregar tarefas.");
    }
  };

  // Função para criar tarefa
  const handleCreateTask = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    if (!newTask.title || !newTask.description || !newTask.category || !newTask.sharedWith) {
      setError('Todos os campos são obrigatórios.');
      return;
    }

    try {
      const response = await axios.post("http://localhost:8000/api/tasks/create/", newTask);
      setTasks([...tasks, response.data]);
      setNewTask({ title: '', description: '', category: '', sharedWith: '' });
      setSuccess("Tarefa criada com sucesso!");
    } catch (err) {
      setError("Erro ao criar tarefa.");
    }
  };

  // Função para excluir tarefa
  const handleDeleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/api/tasks/${id}/delete/`);
      setTasks(tasks.filter(task => task.id !== id));
    } catch (err) {
      setError("Erro ao excluir tarefa.");
    }
  };

  // Função para atualizar tarefa
  const handleUpdateTask = async (id) => {
    // Similar à criação, você pode fazer uma requisição PUT aqui
  };

  return (
    <div>
      <h1>Gerenciamento de Tarefas</h1>
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">{success}</p>}

      <div>
        <h2>Criar Nova Tarefa</h2>
        <form onSubmit={handleCreateTask}>
          <div>
            <label>Título</label>
            <input
              type="text"
              placeholder="Título da tarefa"
              value={newTask.title}
              onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
              required
            />
          </div>
          <div>
            <label>Descrição</label>
            <input
              type="text"
              placeholder="Descrição da tarefa"
              value={newTask.description}
              onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
              required
            />
          </div>
          <div>
            <label>Categoria</label>
            <input
              type="text"
              placeholder="Categoria"
              value={newTask.category}
              onChange={(e) => setNewTask({ ...newTask, category: e.target.value })}
              required
            />
          </div>
          <div>
            <label>Compartilhar com</label>
            <input
              type="text"
              placeholder="E-mails separados por vírgula"
              value={newTask.sharedWith}
              onChange={(e) => setNewTask({ ...newTask, sharedWith: e.target.value })}
              required
            />
          </div>
          <button type="submit">Criar Tarefa</button>
        </form>
      </div>

      <div>
        <h2>Minhas Tarefas</h2>
        {tasks.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Título</th>
                <th>Descrição</th>
                <th>Categoria</th>
                <th>Compartilhar com</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {tasks.map((task) => (
                <tr key={task.id}>
                  <td>{task.title}</td>
                  <td>{task.description}</td>
                  <td>{task.category}</td>
                  <td>{task.sharedWith}</td>
                  <td>
                    <button onClick={() => handleDeleteTask(task.id)}>Excluir</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Nenhuma tarefa encontrada.</p>
        )}
      </div>
    </div>
  );
};

export default TaskList;
