import React, { useEffect, useState } from "react";
import "./TaskList.css";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [newTask, setNewTask] = useState({ title: "", description: "", category: "" });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const fetchTasksAndCategories = async () => {
    try {
      const token = localStorage.getItem("accessToken");

      const [tasksResponse, categoriesResponse] = await Promise.all([
        fetch("http://127.0.0.1:8000/api/tasks/", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }),
        fetch("http://127.0.0.1:8000/api/categories/", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }),
      ]);

      if (!tasksResponse.ok || !categoriesResponse.ok) {
        throw new Error("Erro ao carregar tarefas ou categorias.");
      }

      const tasksData = await tasksResponse.json();
      const categoriesData = await categoriesResponse.json();

      setTasks(tasksData);
      setCategories(categoriesData);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleCreateTask = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    try {
      const token = localStorage.getItem("accessToken");
      const response = await fetch("http://127.0.0.1:8000/api/tasks/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newTask),
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.detail || "Erro ao criar tarefa.");
      }

      setNewTask({ title: "", description: "", category: "" });
      setSuccess("Tarefa criada com sucesso!");
      fetchTasksAndCategories();
    } catch (err) {
      setError(err.message);
    }
  };

  useEffect(() => {
    fetchTasksAndCategories();
  }, []);

  return (
    <>
      <nav>
        <a href="/tasks">Tarefas</a>
        <a href="/login">Login</a>
        <a href="/register">Registrar</a>
      </nav>
      <div className="tasklist-container">
        <h1>Gerenciamento de Tarefas</h1>
        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}

        <div className="tasklist-box">
          <h2>Criar Nova Tarefa</h2>
          <form onSubmit={handleCreateTask}>
            <div className="form-group">
              <label>Título</label>
              <input
                type="text"
                placeholder="Título da tarefa"
                value={newTask.title}
                onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                required
              />
            </div>
            <div className="form-group">
              <label>Descrição</label>
              <input
                type="text"
                placeholder="Descrição da tarefa"
                value={newTask.description}
                onChange={(e) =>
                  setNewTask({ ...newTask, description: e.target.value })
                }
                required
              />
            </div>
            <div className="form-group">
              <label>Categoria</label>
              <select
                value={newTask.category}
                onChange={(e) => setNewTask({ ...newTask, category: e.target.value })}
                required
              >
                <option value="">Selecione uma categoria</option>
                {categories.map((category) => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
            </div>
            <button className="btn-create-task" type="submit">
              Criar Tarefa
            </button>
          </form>
        </div>

        <div className="tasklist-display">
          <h2>Minhas Tarefas</h2>
          {tasks.length > 0 ? (
            <table className="tasklist-table">
              <thead>
                <tr>
                  <th>Título</th>
                  <th>Descrição</th>
                  <th>Categoria</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {tasks.map((task) => (
                  <tr key={task.id}>
                    <td>{task.title}</td>
                    <td>{task.description}</td>
                    <td>
                      {categories.find((category) => category.id === task.category)?.name ||
                        "Sem categoria"}
                    </td>
                    <td>
                      {task.is_completed ? (
                        <span className="status-completed">Concluída</span>
                      ) : (
                        <span className="status-pending">Pendente</span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>Nenhuma tarefa encontrada.</p>
          )}
        </div>
      </div>
    </>
  );
};

export default TaskList;
