import React, { useState, useEffect } from "react";
import axios from "axios";
import "./TaskList.css";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    category: "",
    sharedWith: [],
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Recuperar o token JWT do localStorage
  const token = localStorage.getItem("token");

  // Garantir que o token seja sempre recuperado e configurado corretamente
  if (!token) {
    return <p>Você precisa estar logado para acessar essa página.</p>;
  }

  // Definir o cabeçalho de autenticação para as requisições
  const headers = {
    Authorization: `Bearer ${token}`,
  };

  // Função para buscar as tarefas
  const fetchTasks = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/tasks/", { headers });
      setTasks(response.data);
    } catch (err) {
      console.error("Erro ao buscar tarefas:", err);
      setError("Erro ao carregar as tarefas.");
    }
  };

  // Função para buscar categorias
  const fetchCategories = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/categories/", { headers });
      setCategories(response.data);
    } catch (err) {
      console.error("Erro ao buscar categorias:", err);
      setError("Erro ao carregar as categorias.");
    }
  };

  // Função para criar tarefa
  const handleCreateTask = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!newTask.category) {
      setError("Selecione uma categoria para criar a tarefa.");
      return;
    }

    if (!newTask.sharedWith || newTask.sharedWith.length === 0) {
      setError("Adicione um ou mais usuários para compartilhar a tarefa.");
      return;
    }

    try {
      const taskData = {
        title: newTask.title,
        description: newTask.description,
        category: newTask.category,
        shared_with: newTask.sharedWith,
      };
      await axios.post("http://localhost:8000/api/tasks/", taskData, { headers });
      setSuccess("Tarefa criada e compartilhada com sucesso!");
      fetchTasks(); // Recarrega a lista de tarefas
      setNewTask({ title: "", description: "", category: "", sharedWith: [] });
    } catch (err) {
      setError("Erro ao criar tarefa.");
    }
  };

  // Função para excluir tarefa
  const handleDeleteTask = async (taskId) => {
    try {
      await axios.delete(`http://localhost:8000/api/tasks/${taskId}/`, { headers });
      setSuccess("Tarefa excluída com sucesso!");
      fetchTasks(); // Recarrega a lista de tarefas
    } catch (err) {
      setError("Erro ao excluir tarefa.");
    }
  };

  // Função para atualizar tarefa
  const handleUpdateTask = async (taskId, updatedData) => {
    try {
      await axios.put(`http://localhost:8000/api/tasks/${taskId}/`, updatedData, { headers });
      setSuccess("Tarefa atualizada com sucesso!");
      fetchTasks(); // Recarrega a lista de tarefas
    } catch (err) {
      setError("Erro ao atualizar tarefa.");
    }
  };

  // Efeito para carregar as tarefas e categorias ao montar o componente
  useEffect(() => {
    fetchTasks();
    fetchCategories();
  }, []); // O useEffect agora não é mais condicional e será executado apenas uma vez

  return (
    <div className="tasklist-container">
      <h1>Gerenciamento de Tarefas</h1>
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">{success}</p>}

      <div className="tasklist-box">
        <h2>Criar Nova Tarefa</h2>
        <form onSubmit={handleCreateTask}>
          <div className="form-group">
            <label>Título</label>
            <input
              type="text"
              placeholder="Título da tarefa"
              value={newTask.title}
              onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>Descrição</label>
            <input
              type="text"
              placeholder="Descrição da tarefa"
              value={newTask.description}
              onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>Categoria</label>
            <select
              value={newTask.category}
              onChange={(e) => setNewTask({ ...newTask, category: e.target.value })}
              required
            >
              <option value="">Selecione uma categoria</option>
              {categories.map((category) => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Compartilhar com</label>
            <input
              type="text"
              placeholder="IDs dos usuários, separados por vírgula"
              value={newTask.sharedWith}
              onChange={(e) =>
                setNewTask({
                  ...newTask,
                  sharedWith: e.target.value.split(",").map((id) => id.trim()),
                })
              }
              required
            />
          </div>
          <button className="btn-create-task" type="submit">
            Criar Tarefa
          </button>
        </form>
      </div>

      <div className="tasklist-display">
        <h2>Minhas Tarefas</h2>
        {tasks.length > 0 ? (
          <table className="tasklist-table">
            <thead>
              <tr>
                <th>Título</th>
                <th>Data de Entrega</th>
                <th>Status</th>
                <th>Tempo de Trabalho</th>
                <th>Responsável</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {tasks.map((task) => (
                <tr key={task.id}>
                  <td>{task.title}</td>
                  <td>{new Date(task.created_at).toLocaleDateString()}</td>
                  <td>{task.is_completed ? "Concluída" : "Pendente"}</td>
                  <td>00:45:14</td>
                  <td>{task.shared_with.join(", ")}</td>
                  <td>
                    <button onClick={() => handleDeleteTask(task.id)}>Excluir</button>
                    <button onClick={() => handleUpdateTask(task.id, { is_completed: true })}>
                      Concluir
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Nenhuma tarefa encontrada.</p>
        )}
      </div>
    </div>
  );
};

export default TaskList;
