import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import "./ForgotPassword.css";

const ForgotPassword = () => {
  const [email, setEmail] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const history = useHistory();

  // Função para lidar com o envio do e-mail de recuperação
  const handleSubmit = (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!email) {
      setError("Por favor, insira um e-mail.");
      return;
    }

    // Simulação do envio de e-mail para recuperação de senha
    try {
      // Aqui você poderia integrar com a API do backend para enviar o e-mail
      setSuccess("E-mail de recuperação enviado com sucesso!");
      setEmail(""); // Limpa o campo de e-mail
      // Redireciona para a página de login após o envio
      setTimeout(() => {
        history.push("/login");
      }, 3000);
    } catch (err) {
      setError("Erro ao enviar o e-mail. Tente novamente.");
    }
  };

  return (
    <div className="forgot-password-container">
      <header className="header">
        <nav>
          <ul className="nav-links">
            <li><a href="/login">Login</a></li>
            <li><a href="/register">Registrar</a></li>
          </ul>
        </nav>
      </header>

      <h1>Recuperação de Senha</h1>
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">{success}</p>}

      <div className="forgot-password-box">
        <h2>Esqueceu sua senha?</h2>
        <p>Informe seu e-mail abaixo e enviaremos um link para você redefinir sua senha.</p>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>E-mail</label>
            <input
              type="email"
              placeholder="Digite seu e-mail"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <button className="btn-submit" type="submit">
            Enviar Link de Recuperação
          </button>
        </form>
      </div>
    </div>
  );
};

export default ForgotPassword;
