import React, { useEffect, useState } from 'react';
import './TaskList.css';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ title: '', description: '' });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Função para buscar tarefas do backend
  const fetchTasks = async () => {
    try {
      const token = localStorage.getItem('accessToken');
      const response = await fetch('http://127.0.0.1:8000/api/tasks/', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error('Erro ao carregar tarefas. Verifique sua conexão.');
      }

      const data = await response.json();
      setTasks(data);
    } catch (err) {
      setError(err.message);
    }
  };

  // Função para criar nova tarefa
  const handleCreateTask = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const token = localStorage.getItem('accessToken');
      const response = await fetch('http://127.0.0.1:8000/api/tasks/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newTask),
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.detail || 'Erro ao criar tarefa. Verifique os dados.');
      }

      setNewTask({ title: '', description: '' });
      setSuccess('Tarefa criada com sucesso!');
      fetchTasks(); // Atualiza a lista de tarefas
    } catch (err) {
      setError(err.message);
    }
  };

  // Efeito para buscar tarefas quando o componente monta
  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div className="tasklist-container">
      <h1>Gerenciamento de Tarefas</h1>
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">{success}</p>}

      <div className="tasklist-box">
        <h2>Criar Nova Tarefa</h2>
        <form onSubmit={handleCreateTask}>
          <div className="form-group">
            <label>Título</label>
            <input
              type="text"
              placeholder="Título da tarefa"
              value={newTask.title}
              onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>Descrição</label>
            <input
              type="text"
              placeholder="Descrição da tarefa"
              value={newTask.description}
              onChange={(e) =>
                setNewTask({ ...newTask, description: e.target.value })
              }
              required
            />
          </div>
          <button className="btn-create-task" type="submit">
            Criar Tarefa
          </button>
        </form>
      </div>

      <div className="tasklist-display">
        <h2>Minhas Tarefas</h2>
        {tasks.length > 0 ? (
          <table className="tasklist-table">
            <thead>
              <tr>
                <th>Título</th>
                <th>Descrição</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {tasks.map((task) => (
                <tr key={task.id}>
                  <td>{task.title}</td>
                  <td>{task.description}</td>
                  <td>
                    {task.is_completed ? (
                      <span className="status-completed">Concluída</span>
                    ) : (
                      <span className="status-pending">Pendente</span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Nenhuma tarefa encontrada.</p>
        )}
      </div>
    </div>
  );
};

export default TaskList;
