import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import TaskList from './pages/TaskList';
import Login from './pages/Login';
import Register from './pages/Register';
import Weather from './components/Weather';
import Clock from './components/Clock';
import Map from './components/Map';
import './App.css';  // Importando o CSS

const App = () => {
  return (
    <Router>
      <div className="app-container">
        {/* Header */}
        <header>
          <nav>
            <Link className="nav-link" to="/tasks">Tarefas</Link>
            <Link className="nav-link" to="/login">Login</Link>
            <Link className="nav-link" to="/register">Registrar</Link>
          </nav>
        </header>

        {/* Main content */}
        <main>
          <div className="layout">
            {/* Mapa no lado esquerdo */}
            <div className="map-container">
              <Map />
            </div>

            {/* Relógio no canto superior direito */}
            <div className="clock-container">
              <Clock />
            </div>

            {/* Clima abaixo do mapa */}
            <div className="weather-container">
              <Weather />
            </div>
          </div>

          {/* Rotas para as páginas de login, tarefas e registro */}
          <Routes>
            <Route path="/tasks" element={<TaskList />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/" element={<TaskList />} />
          </Routes>
        </main>

        {/* Footer */}
        <footer>
          <div className="footer-content">
            <div className="subscribe-form">
              <input type="email" placeholder="Seu e-mail" />
              <button>Inscrever-se</button>
            </div>
            <div className="social-icons">
              <a href="https://facebook.com">F</a>
              <a href="https://twitter.com">T</a>
              <a href="https://instagram.com">I</a>
            </div>
          </div>
        </footer>
      </div>
    </Router>
  );
};

export default App;
