import React, { useState } from "react";
import "./TaskList.css";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([
    { id: 1, name: "Trabalho" },
    { id: 2, name: "Pessoal" },
  ]);
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    category: "",
    sharedWith: "",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleCreateTask = (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!newTask.category) {
      setError("Selecione uma categoria.");
      return;
    }

    if (!newTask.sharedWith) {
      setError("Adicione um responsável.");
      return;
    }

    setTasks([...tasks, newTask]);
    setNewTask({ title: "", description: "", category: "", sharedWith: "" });
    setSuccess("Tarefa criada com sucesso!");
  };

  return (
    <div className="tasklist-container">
      {/* Menu Lateral */}
      <div className="sidebar">
        <h2>Menu</h2>
        <ul>
          <li>Tarefas</li>
          <li>Nova Tarefa</li>
          <li>Progresso</li>
        </ul>
      </div>

      {/* Conteúdo Principal */}
      <div className="main-content">
        <h1>Gerenciador de Tarefas</h1>
        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}

        <form onSubmit={handleCreateTask} className="task-form">
          <div>
            <label>Título</label>
            <input
              type="text"
              value={newTask.title}
              onChange={(e) =>
                setNewTask({ ...newTask, title: e.target.value })
              }
              required
            />
          </div>
          <div>
            <label>Descrição</label>
            <input
              type="text"
              value={newTask.description}
              onChange={(e) =>
                setNewTask({ ...newTask, description: e.target.value })
              }
            />
          </div>
          <div>
            <label>Categoria</label>
            <select
              value={newTask.category}
              onChange={(e) =>
                setNewTask({ ...newTask, category: e.target.value })
              }
              required
            >
              <option value="">Selecione</option>
              {categories.map((cat) => (
                <option key={cat.id} value={cat.id}>
                  {cat.name}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label>Compartilhar com</label>
            <input
              type="text"
              placeholder="E-mail ou nome"
              value={newTask.sharedWith}
              onChange={(e) =>
                setNewTask({ ...newTask, sharedWith: e.target.value })
              }
              required
            />
          </div>
          <button type="submit">Criar Tarefa</button>
        </form>

        {/* Tabela de Tarefas */}
        <h2>Tarefas</h2>
        <table className="task-table">
          <thead>
            <tr>
              <th>Título</th>
              <th>Descrição</th>
              <th>Categoria</th>
              <th>Responsável</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map((task, index) => (
              <tr key={index}>
                <td>{task.title}</td>
                <td>{task.description}</td>
                <td>{task.category}</td>
                <td>{task.sharedWith}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default TaskList;
