import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./TaskList.css";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    category: "",
    sharedWith: [],
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(true);

  const navigate = useNavigate();

  // Recuperar o token JWT do localStorage
  const token = localStorage.getItem("token");
  const headers = token
    ? { Authorization: `Bearer ${token}` }
    : null;

  // Redirecionar para login se não houver token
  useEffect(() => {
    if (!token) {
      setError("Você precisa estar logado para acessar essa página.");
      setTimeout(() => navigate("/login"), 2000); // Redireciona após 2 segundos
    } else {
      fetchTasks();
      fetchCategories();
    }
  }, [token]);

  // Função para buscar tarefas
  const fetchTasks = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/api/tasks/", { headers });
      setTasks(response.data);
    } catch (err) {
      handleAuthError(err, "Erro ao carregar as tarefas.");
    }
  };

  // Função para buscar categorias
  const fetchCategories = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/api/categories/", { headers });
      setCategories(response.data);
      setLoading(false);
    } catch (err) {
      handleAuthError(err, "Erro ao carregar as categorias.");
    }
  };

  // Função para lidar com erros de autenticação
  const handleAuthError = (err, fallbackMessage) => {
    if (err.response && err.response.status === 401) {
      setError("Sua sessão expirou. Faça login novamente.");
      localStorage.removeItem("token");
      setTimeout(() => navigate("/login"), 2000);
    } else {
      setError(fallbackMessage);
    }
    setLoading(false);
  };

  // Função para criar tarefa
  const handleCreateTask = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!newTask.category) {
      setError("Selecione uma categoria para criar a tarefa.");
      return;
    }

    if (!newTask.sharedWith || newTask.sharedWith.length === 0) {
      setError("Adicione um ou mais usuários para compartilhar a tarefa.");
      return;
    }

    try {
      const taskData = {
        title: newTask.title,
        description: newTask.description,
        category: newTask.category,
        shared_with: newTask.sharedWith,
      };
      await axios.post("http://127.0.0.1:8000/api/tasks/", taskData, { headers });
      setSuccess("Tarefa criada com sucesso!");
      fetchTasks();
      setNewTask({ title: "", description: "", category: "", sharedWith: [] });
    } catch (err) {
      handleAuthError(err, "Erro ao criar a tarefa.");
    }
  };

  // Função para excluir tarefa
  const handleDeleteTask = async (taskId) => {
    try {
      await axios.delete(`http://127.0.0.1:8000/api/tasks/${taskId}/`, { headers });
      setSuccess("Tarefa excluída com sucesso!");
      fetchTasks();
    } catch (err) {
      handleAuthError(err, "Erro ao excluir tarefa.");
    }
  };

  return (
    <div className="tasklist-container">
      {loading ? (
        <p>Carregando...</p>
      ) : error ? (
        <p className="error-message">{error}</p>
      ) : (
        <>
          <h1>Gerenciamento de Tarefas</h1>
          {success && <p className="success-message">{success}</p>}
          {error && <p className="error-message">{error}</p>}

          {/* Formulário para criar nova tarefa */}
          <div className="tasklist-box">
            <h2>Criar Nova Tarefa</h2>
            <form onSubmit={handleCreateTask}>
              <div className="form-group">
                <label>Título</label>
                <input
                  type="text"
                  placeholder="Título da tarefa"
                  value={newTask.title}
                  onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <label>Descrição</label>
                <input
                  type="text"
                  placeholder="Descrição da tarefa"
                  value={newTask.description}
                  onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <label>Categoria</label>
                <select
                  value={newTask.category}
                  onChange={(e) => setNewTask({ ...newTask, category: e.target.value })}
                  required
                >
                  <option value="">Selecione uma categoria</option>
                  {categories.map((category) => (
                    <option key={category.id} value={category.id}>
                      {category.name}
                    </option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Compartilhar com</label>
                <input
                  type="text"
                  placeholder="IDs dos usuários, separados por vírgula"
                  value={newTask.sharedWith}
                  onChange={(e) =>
                    setNewTask({
                      ...newTask,
                      sharedWith: e.target.value.split(",").map((id) => id.trim()),
                    })
                  }
                  required
                />
              </div>
              <button type="submit">Criar Tarefa</button>
            </form>
          </div>

          {/* Lista de tarefas */}
          <div className="tasklist-display">
            <h2>Minhas Tarefas</h2>
            {tasks.length > 0 ? (
              <ul>
                {tasks.map((task) => (
                  <li key={task.id}>
                    <strong>{task.title}</strong> - {task.description}{" "}
                    <button onClick={() => handleDeleteTask(task.id)}>Excluir</button>
                  </li>
                ))}
              </ul>
            ) : (
              <p>Nenhuma tarefa encontrada.</p>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default TaskList;
