import React, { useState } from "react";
import "./TaskList.css";

const TaskList = () => {
  const [tasks, setTasks] = useState([
    {
      name: "Marketing Coordinator",
      deadline: "10 Dec 2020",
      status: "Active",
      workingTime: "00:45:14",
      assignedTo: "Taslima Nasrim",
    },
    // outras tarefas podem ser adicionadas aqui
  ]);
  const [categories, setCategories] = useState([
    { id: 1, name: "Trabalho" },
    { id: 2, name: "Pessoal" },
  ]);
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    category: "",
    sharedWith: "",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Função para criar tarefa
  const handleCreateTask = (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!newTask.category) {
      setError("Selecione uma categoria para criar a tarefa.");
      return;
    }

    if (!newTask.sharedWith) {
      setError("Adicione um ou mais usuários para compartilhar a tarefa.");
      return;
    }

    // Simulação de criação de tarefa
    setTasks([...tasks, newTask]);
    setNewTask({ title: "", description: "", category: "", sharedWith: "" });
    setSuccess("Tarefa criada e compartilhada com sucesso!");
  };

  return (
    <div className="tasklist-container">
      {/* Barra lateral */}
      <div className="sidebar">
        <h2>Menu</h2>
        <ul>
          <li><a href="/tasks">Tarefas</a></li>
          <li><a href="/login">Login</a></li>
          <li><a href="/register">Registrar</a></li>
        </ul>
      </div>

      {/* Conteúdo principal */}
      <div className="main-content">
        <h1>Gerenciamento de Tarefas</h1>
        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}

        {/* Formulário para criar nova tarefa */}
        <div className="tasklist-box">
          <h2>Criar Nova Tarefa</h2>
          <form onSubmit={handleCreateTask}>
            <div className="form-group">
              <label>Título</label>
              <input
                type="text"
                placeholder="Título da tarefa"
                value={newTask.title}
                onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                required
              />
            </div>
            <div className="form-group">
              <label>Descrição</label>
              <input
                type="text"
                placeholder="Descrição da tarefa"
                value={newTask.description}
                onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
                required
              />
            </div>
            <div className="form-group">
              <label>Categoria</label>
              <select
                value={newTask.category}
                onChange={(e) => setNewTask({ ...newTask, category: e.target.value })}
                required
              >
                <option value="">Selecione uma categoria</option>
                {categories.map((category) => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label>Compartilhar com</label>
              <input
                type="text"
                placeholder="E-mails separados por vírgula"
                value={newTask.sharedWith}
                onChange={(e) => setNewTask({ ...newTask, sharedWith: e.target.value })}
                required
              />
            </div>
            <button className="btn-create-task" type="submit">
              Criar Tarefa
            </button>
          </form>
        </div>

        {/* Tabela de tarefas */}
        <div className="tasklist-display">
          <h2>Minhas Tarefas</h2>
          {tasks.length > 0 ? (
            <table className="tasklist-table">
              <thead>
                <tr>
                  <th>Título</th>
                  <th>Data de Entrega</th>
                  <th>Status</th>
                  <th>Tempo de Trabalho</th>
                  <th>Responsável</th>
                </tr>
              </thead>
              <tbody>
                {tasks.map((task, index) => (
                  <tr key={index}>
                    <td>{task.name}</td>
                    <td>{task.deadline}</td>
                    <td>{task.status}</td>
                    <td>{task.workingTime}</td>
                    <td>{task.assignedTo}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>Nenhuma tarefa encontrada.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default TaskList;
