import React, { useState, useEffect } from "react";
import axios from "axios";
import "./TaskList.css";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ title: "", description: "", category: "", sharedWith: "" });
  const [editTask, setEditTask] = useState(null);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Função para criar nova tarefa
  const handleCreateTask = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!newTask.title || !newTask.description || !newTask.category || !newTask.sharedWith) {
      setError("Todos os campos são obrigatórios.");
      return;
    }

    try {
      // Enviar dados ao backend
      const response = await axios.post("/api/tasks", newTask);
      setTasks([...tasks, response.data]);
      setNewTask({ title: "", description: "", category: "", sharedWith: "" });
      setSuccess("Tarefa criada com sucesso!");
    } catch (err) {
      setError("Erro ao criar tarefa.");
    }
  };

  // Função para listar tarefas
  const fetchTasks = async () => {
    try {
      const response = await axios.get("/api/tasks");
      setTasks(response.data);
    } catch (err) {
      setError("Erro ao carregar tarefas.");
    }
  };

  // Função para excluir tarefa
  const handleDeleteTask = async (taskId) => {
    try {
      await axios.delete(`/api/tasks/${taskId}`);
      setTasks(tasks.filter((task) => task.id !== taskId));
      setSuccess("Tarefa excluída com sucesso!");
    } catch (err) {
      setError("Erro ao excluir tarefa.");
    }
  };

  // Função para editar tarefa
  const handleEditTask = async (task) => {
    setEditTask(task);
  };

  // Função para salvar atualização da tarefa
  const handleUpdateTask = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`/api/tasks/${editTask.id}`, editTask);
      setTasks(tasks.map((task) => (task.id === editTask.id ? response.data : task)));
      setEditTask(null);
      setSuccess("Tarefa atualizada com sucesso!");
    } catch (err) {
      setError("Erro ao atualizar tarefa.");
    }
  };

  // Função para compartilhar tarefa
  const handleShareTask = async (taskId, emailList) => {
    try {
      await axios.post(`/api/tasks/${taskId}/share`, { sharedWith: emailList });
      setSuccess("Tarefa compartilhada com sucesso!");
    } catch (err) {
      setError("Erro ao compartilhar tarefa.");
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div className="tasklist-container">
      <h1>Gerenciamento de Tarefas</h1>

      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">{success}</p>}

      <div className="tasklist-box">
        <h2>Criar Nova Tarefa</h2>
        <form onSubmit={handleCreateTask}>
          <div className="form-group">
            <label>Título</label>
            <input
              type="text"
              placeholder="Título da tarefa"
              value={newTask.title}
              onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
            />
          </div>
          <div className="form-group">
            <label>Descrição</label>
            <input
              type="text"
              placeholder="Descrição da tarefa"
              value={newTask.description}
              onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
            />
          </div>
          <div className="form-group">
            <label>Categoria</label>
            <input
              type="text"
              placeholder="Categoria"
              value={newTask.category}
              onChange={(e) => setNewTask({ ...newTask, category: e.target.value })}
            />
          </div>
          <div className="form-group">
            <label>Compartilhar com</label>
            <input
              type="text"
              placeholder="E-mails separados por vírgula"
              value={newTask.sharedWith}
              onChange={(e) => setNewTask({ ...newTask, sharedWith: e.target.value })}
            />
          </div>
          <button className="btn-create-task" type="submit">
            Criar Tarefa
          </button>
        </form>
      </div>

      {editTask && (
        <div className="tasklist-box">
          <h2>Editar Tarefa</h2>
          <form onSubmit={handleUpdateTask}>
            <div className="form-group">
              <label>Título</label>
              <input
                type="text"
                value={editTask.title}
                onChange={(e) => setEditTask({ ...editTask, title: e.target.value })}
              />
            </div>
            <div className="form-group">
              <label>Descrição</label>
              <input
                type="text"
                value={editTask.description}
                onChange={(e) => setEditTask({ ...editTask, description: e.target.value })}
              />
            </div>
            <div className="form-group">
              <label>Categoria</label>
              <input
                type="text"
                value={editTask.category}
                onChange={(e) => setEditTask({ ...editTask, category: e.target.value })}
              />
            </div>
            <div className="form-group">
              <label>Compartilhar com</label>
              <input
                type="text"
                value={editTask.sharedWith}
                onChange={(e) => setEditTask({ ...editTask, sharedWith: e.target.value })}
              />
            </div>
            <button className="btn-create-task" type="submit">
              Atualizar Tarefa
            </button>
          </form>
        </div>
      )}

      <div className="tasklist-display">
        <h2>Minhas Tarefas</h2>
        {tasks.length > 0 ? (
          <table className="tasklist-table">
            <thead>
              <tr>
                <th>Título</th>
                <th>Descrição</th>
                <th>Categoria</th>
                <th>Compartilhar com</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {tasks.map((task, index) => (
                <tr key={index}>
                  <td>{task.title}</td>
                  <td>{task.description}</td>
                  <td>{task.category}</td>
                  <td>{task.sharedWith}</td>
                  <td>
                    <button onClick={() => handleEditTask(task)}>Editar</button>
                    <button onClick={() => handleDeleteTask(task.id)}>Excluir</button>
                    <button onClick={() => handleShareTask(task.id, task.sharedWith)}>
                      Compartilhar
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Nenhuma tarefa encontrada.</p>
        )}
      </div>
    </div>
  );
};

export default TaskList;
