import React, { useState, useEffect } from "react";
import axios from "axios";
import "./TaskList.css";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    category: "",
    sharedWith: "",
  });
  const [categories, setCategories] = useState([
    { id: 1, name: "Trabalho" },
    { id: 2, name: "Pessoal" },
  ]);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Função para buscar tarefas
  const fetchTasks = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/tasks", {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setTasks(response.data);
    } catch (err) {
      console.error("Erro ao buscar tarefas", err);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  // Função para criar tarefa
  const handleCreateTask = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!newTask.category) {
      setError("Selecione uma categoria para criar a tarefa.");
      return;
    }

    if (!newTask.sharedWith) {
      setError("Adicione um ou mais usuários para compartilhar a tarefa.");
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:8000/api/tasks/create",
        newTask,
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      setTasks([...tasks, response.data]);
      setNewTask({ title: "", description: "", category: "", sharedWith: "" });
      setSuccess("Tarefa criada e compartilhada com sucesso!");
    } catch (err) {
      setError("Erro ao criar tarefa.");
    }
  };

  // Função para excluir tarefa
  const handleDeleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/api/tasks/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setTasks(tasks.filter((task) => task.id !== id));
      setSuccess("Tarefa excluída com sucesso!");
    } catch (err) {
      setError("Erro ao excluir tarefa.");
    }
  };

  // Função para atualizar tarefa
  const handleUpdateTask = async (taskId, updatedTask) => {
    try {
      const response = await axios.put(
        `http://localhost:8000/api/tasks/${taskId}`,
        updatedTask,
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      const updatedTasks = tasks.map((task) =>
        task.id === taskId ? response.data : task
      );
      setTasks(updatedTasks);
      setSuccess("Tarefa atualizada com sucesso!");
    } catch (err) {
      setError("Erro ao atualizar tarefa.");
    }
  };

  return (
    <div className="tasklist-container">
      <h1>Gerenciamento de Tarefas</h1>
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">{success}</p>}

      <div className="tasklist-box">
        <h2>Criar Nova Tarefa</h2>
        <form onSubmit={handleCreateTask}>
          <div className="form-group">
            <label>Título</label>
            <input
              type="text"
              placeholder="Título da tarefa"
              value={newTask.title}
              onChange={(e) =>
                setNewTask({ ...newTask, title: e.target.value })
              }
              required
            />
          </div>
          <div className="form-group">
            <label>Descrição</label>
            <input
              type="text"
              placeholder="Descrição da tarefa"
              value={newTask.description}
              onChange={(e) =>
                setNewTask({ ...newTask, description: e.target.value })
              }
              required
            />
          </div>
          <div className="form-group">
            <label>Categoria</label>
            <select
              value={newTask.category}
              onChange={(e) => setNewTask({ ...newTask, category: e.target.value })}
              required
            >
              <option value="">Selecione uma categoria</option>
              {categories.map((category) => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Compartilhar com</label>
            <input
              type="text"
              placeholder="E-mails separados por vírgula"
              value={newTask.sharedWith}
              onChange={(e) =>
                setNewTask({ ...newTask, sharedWith: e.target.value })
              }
              required
            />
          </div>
          <button className="btn-create-task" type="submit">
            Criar Tarefa
          </button>
        </form>
      </div>

      <div className="tasklist-display">
        <h2>Minhas Tarefas</h2>
        {tasks.length > 0 ? (
          <table className="tasklist-table">
            <thead>
              <tr>
                <th>Título</th>
                <th>Data de Entrega</th>
                <th>Status</th>
                <th>Tempo de Trabalho</th>
                <th>Responsável</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {tasks.map((task) => (
                <tr key={task.id}>
                  <td>{task.title}</td>
                  <td>{task.dueDate}</td>
                  <td>{task.status}</td>
                  <td>{task.workTime}</td>
                  <td>{task.sharedWith}</td>
                  <td>
                    <button
                      onClick={() => handleUpdateTask(task.id, task)}
                      className="btn-update"
                    >
                      Atualizar
                    </button>
                    <button
                      onClick={() => handleDeleteTask(task.id)}
                      className="btn-delete"
                    >
                      Excluir
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Nenhuma tarefa encontrada.</p>
        )}
      </div>
    </div>
  );
};

export default TaskList;
