import React, { useEffect, useState } from "react";
import "./TaskList.css";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]); // Inicializado como array vazio
  const [newTask, setNewTask] = useState({ title: "", description: "", category: "" });
  const [shareEmail, setShareEmail] = useState("");
  const [selectedCategory, setSelectedCategory] = useState(""); // Estado para a categoria selecionada
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [user, setUser] = useState(null); // Nome do usuário logado

  // Fetch o usuário logado
  const fetchUser = async () => {
    try {
      const token = localStorage.getItem("accessToken");
      const response = await fetch("http://127.0.0.1:8000/api/user/", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (response.ok) {
        const data = await response.json();
        setUser(data.username);
      }
    } catch (err) {
      console.error("Erro ao carregar o usuário logado:", err.message);
    }
  };

  // Fetch tasks from backend
  const fetchTasks = async () => {
    try {
      const token = localStorage.getItem("accessToken");
      const response = await fetch("http://127.0.0.1:8000/api/tasks/", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) throw new Error("Erro ao carregar tarefas.");

      const data = await response.json();
      setTasks(data);
    } catch (err) {
      setError(err.message);
    }
  };

  // Fetch categories from backend
  const fetchCategories = async () => {
    try {
      const token = localStorage.getItem("accessToken");
      const response = await fetch("http://127.0.0.1:8000/api/categories/", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) throw new Error("Erro ao carregar categorias.");

      const data = await response.json();
      // Garantir que categories seja um array
      if (Array.isArray(data)) {
        setCategories(data);
      } else {
        setCategories([]); // Se não for um array, definir como vazio
      }
    } catch (err) {
      setError(err.message);
      setCategories([]); // Evitar falhas ao inicializar
    }
  };

  // Create a new task
  const handleCreateTask = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!newTask.category) {
      setError("Selecione uma categoria para criar a tarefa.");
      return;
    }

    try {
      const token = localStorage.getItem("accessToken");
      const response = await fetch("http://127.0.0.1:8000/api/tasks/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newTask),
      });

      if (!response.ok) throw new Error("Erro ao criar tarefa.");

      setNewTask({ title: "", description: "", category: "" });
      setSuccess("Tarefa criada com sucesso!");
      fetchTasks();
    } catch (err) {
      setError(err.message);
    }
  };

  // Share a task with another user
  const handleShareTask = async (taskId) => {
    setError("");
    setSuccess("");

    try {
      const token = localStorage.getItem("accessToken");
      const response = await fetch(`http://127.0.0.1:8000/api/tasks/${taskId}/share/`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ shared_with: [shareEmail] }),
      });

      if (!response.ok) throw new Error("Erro ao compartilhar tarefa.");

      setShareEmail("");
      setSuccess("Tarefa compartilhada com sucesso!");
    } catch (err) {
      setError(err.message);
    }
  };

  useEffect(() => {
    fetchUser();
    fetchTasks();
    fetchCategories();
  }, []);

  const filteredTasks = selectedCategory
    ? tasks.filter((task) => task.category === selectedCategory)
    : tasks;

  return (
    <div className="tasklist-container">
      <header className="header">
        <nav>
          <ul className="nav-links">
            <li><a href="/tasks">Tarefas</a></li>
            {!user ? (
              <>
                <li><a href="/login">Login</a></li>
                <li><a href="/register">Registrar</a></li>
              </>
            ) : (
              <li><a href="/logout">Logout</a></li>
            )}
          </ul>
        </nav>
        {user && <p className="user-info">Usuário logado: {user}</p>}
      </header>

      <h1>Gerenciamento de Tarefas</h1>
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">{success}</p>}

      <div className="tasklist-box">
        <h2>Criar Nova Tarefa</h2>
        <form onSubmit={handleCreateTask}>
          <div className="form-group">
            <label>Título</label>
            <input
              type="text"
              placeholder="Título da tarefa"
              value={newTask.title}
              onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>Descrição</label>
            <input
              type="text"
              placeholder="Descrição da tarefa"
              value={newTask.description}
              onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>Categoria</label>
            <select
              value={newTask.category}
              onChange={(e) => setNewTask({ ...newTask, category: e.target.value })}
              required
            >
              <option value="">Selecione uma categoria</option>
              {Array.isArray(categories) && categories.map((category) => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>
          <button className="btn-create-task" type="submit">
            Criar Tarefa
          </button>
        </form>
      </div>

      <div className="tasklist-display">
        <h2>Minhas Tarefas</h2>
        <div className="filter-category">
          <label>Filtrar por Categoria:</label>
          <select onChange={(e) => setSelectedCategory(e.target.value)}>
            <option value="">Todas</option>
            {categories.map((category) => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>
        {filteredTasks.length > 0 ? (
          <table className="tasklist-table">
            <thead>
              <tr>
                <th>Título</th>
                <th>Descrição</th>
                <th>Categoria</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {filteredTasks.map((task) => (
                <tr key={task.id}>
                  <td>{task.title}</td>
                  <td>{task.description}</td>
                  <td>{task.category ? categories.find((c) => c.id === task.category)?.name : "Sem categoria"}</td>
                  <td>{task.is_completed ? "Concluída" : "Pendente"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Nenhuma tarefa encontrada.</p>
        )}
      </div>
    </div>
  );
};

export default TaskList;
