import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import "./TaskList.css";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [categories] = useState([
    { id: 1, name: "Trabalho" },
    { id: 2, name: "Estudos" },
    { id: 3, name: "Lazer" },
  ]); // Categorias fixas no front-end
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    category: "",
    sharedWith: [],
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(true);
  const [selectedTask, setSelectedTask] = useState(null);

  const token = localStorage.getItem("token");
  const headers = token ? { Authorization: `Bearer ${token}` } : null;

  // Função para buscar tarefas
  const fetchTasks = useCallback(async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/tasks/", { headers });
      setTasks(response.data.results || response.data); // Suporte a paginação
    } catch (err) {
      setError("Erro ao buscar as tarefas. Verifique o servidor.");
    }
  }, [headers]);

  // Função para criar uma tarefa
  const handleCreateTask = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!newTask.category) {
      setError("Selecione uma categoria.");
      return;
    }

    try {
      const taskData = {
        title: newTask.title,
        description: newTask.description,
        category: newTask.category,
        shared_with: newTask.sharedWith,
      };

      await axios.post("http://localhost:8000/api/tasks/", taskData, { headers });
      setSuccess("Tarefa criada com sucesso!");
      fetchTasks();
      setNewTask({ title: "", description: "", category: "", sharedWith: [] });
    } catch (err) {
      setError("Erro ao criar a tarefa. Verifique os dados.");
    }
  };

  // Função para excluir uma tarefa
  const handleDeleteTask = async (taskId) => {
    try {
      await axios.delete(`http://localhost:8000/api/tasks/${taskId}/`, { headers });
      setSuccess("Tarefa excluída com sucesso!");
      fetchTasks();
    } catch (err) {
      setError("Erro ao excluir a tarefa.");
    }
  };

  // Função para atualizar uma tarefa
  const handleUpdateTask = async (taskId, updatedData) => {
    try {
      await axios.put(`http://localhost:8000/api/tasks/${taskId}/`, updatedData, { headers });
      setSuccess("Tarefa atualizada com sucesso!");
      fetchTasks();
    } catch (err) {
      setError("Erro ao atualizar a tarefa.");
    }
  };

  // Carregar tarefas
  useEffect(() => {
    if (headers) {
      fetchTasks();
      setLoading(false);
    } else {
      setError("Você precisa estar logado.");
      setLoading(false);
    }
  }, [headers, fetchTasks]);

  return (
    <div className="tasklist-container">
      {loading ? (
        <p>Carregando...</p>
      ) : (
        <>
          <h1>Gerenciamento de Tarefas</h1>
          {error && <p className="error-message">{error}</p>}
          {success && <p className="success-message">{success}</p>}

          {/* Formulário para criar ou editar tarefa */}
          <div className="tasklist-box">
            <h2>{selectedTask ? "Editar Tarefa" : "Criar Nova Tarefa"}</h2>
            <form onSubmit={handleCreateTask}>
              <div className="form-group">
                <label>Título</label>
                <input
                  type="text"
                  placeholder="Título da tarefa"
                  value={newTask.title}
                  onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <label>Descrição</label>
                <input
                  type="text"
                  placeholder="Descrição"
                  value={newTask.description}
                  onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <label>Categoria</label>
                <select
                  value={newTask.category}
                  onChange={(e) => setNewTask({ ...newTask, category: e.target.value })}
                  required
                >
                  <option value="">Selecione uma categoria</option>
                  {categories.map((category) => (
                    <option key={category.id} value={category.id}>
                      {category.name}
                    </option>
                  ))}
                </select>
              </div>
              <button type="submit" className="btn-create-task">
                {selectedTask ? "Atualizar Tarefa" : "Criar Tarefa"}
              </button>
            </form>
          </div>

          {/* Lista de tarefas */}
          <div className="tasklist-display">
            <h2>Minhas Tarefas</h2>
            {tasks.length > 0 ? (
              <table className="tasklist-table">
                <thead>
                  <tr>
                    <th>Título</th>
                    <th>Data de Criação</th>
                    <th>Status</th>
                    <th>Ações</th>
                  </tr>
                </thead>
                <tbody>
                  {tasks.map((task) => (
                    <tr key={task.id}>
                      <td>{task.title}</td>
                      <td>{new Date(task.created_at).toLocaleDateString()}</td>
                      <td>{task.is_completed ? "Concluída" : "Pendente"}</td>
                      <td>
                        <button onClick={() => handleDeleteTask(task.id)}>Excluir</button>
                        <button
                          onClick={() =>
                            handleUpdateTask(task.id, { is_completed: !task.is_completed })
                          }
                        >
                          {task.is_completed ? "Reabrir" : "Concluir"}
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p>Nenhuma tarefa encontrada.</p>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default TaskList;
